(39, 'storefront-customize', 'info', 'en_US', 'Design your store with Storefront 🎨', 'Visit the Storefront settings page to start setup and customization of your shop.', '{}', 'unactioned', 'storefront', '2021-09-15 04:01:07', NULL, 0, 'plain', '', 0, 'info'),
(40, 'wc-admin-learn-more-about-variable-products', 'info', 'en_US', 'Learn more about variable products', 'Variable products are a powerful product type that lets you offer a set of variations on a product, with control over prices, stock, image and more for each variation. They can be used for a product like a shirt, where you can offer a large, medium and small and in different colors.', '{}', 'unactioned', 'woocommerce-admin', '2021-09-15 09:18:18', NULL, 0, 'plain', '', 0, 'info'),
(41, 'wc-admin-start-dropshipping-business', 'info', 'en_US', 'Are you considering starting a dropshipping business?', 'The ability to add inventory without having to deal with production, stocking, or fulfilling orders may seem like a dream. But is dropshipping worth it? Let’s explore some of the advantages and disadvantages to help you make the best decision for your business.', '{}', 'unactioned', 'woocommerce-admin', '2021-09-16 04:24:42', NULL, 0, 'plain', '', 0, 'info'),
(42, 'wc-admin-choose-niche', 'info', 'en_US', 'Finding an audience for your business', 'There’s nothing quite like entrepreneurship! We put together a blog post series — titled From Idea to First Customer — with all the tips, resources, and tools to inspire a great idea, empower you to execute it, and lead to your very first sale.', '{}', 'unactioned', 'woocommerce-admin', '2021-09-16 04:24:42', NULL, 0, 'plain', '', 0, 'info'),
(43, 'wc-admin-choosing-a-theme', 'marketing', 'en_US', 'Choosing a theme?', 'Check out the themes that are compatible with WooCommerce and choose one aligned with your brand and business needs.', '{}', 'unactioned', 'woocommerce-admin', '2021-09-16 04:24:42', NULL, 0, 'plain', '', 0, 'info'),
(44, 'wc-admin-insight-first-product-and-payment', 'survey', 'en_US', 'Insight', 'More than 80% of new merchants add the first product and have at least one payment method set up during the first week.<br><br>Do you find this type of insight useful?', '{}', 'unactioned', 'woocommerce-admin', '2021-09-16 04:24:42', NULL, 0, 'plain', '', 0, 'info'),
